mapper.select:
SELECT id, user_id, section, question, answer, customer_id, write_date, is_deleted, created_at, created_by, updated_at, updated_by
FROM faq
WHERE id = #{id}

mapper.update:
UPDATE faq
SET is_deleted = 1
WHERE id = #{id}

UPDATE faq
SET is_deleted = 0
WHERE id = #{id}

mapper.select:
